typedef unsigned char* font_pixel;
typedef unsigned char  font_pixel_line;

void graphics_draw_font(const char font,graphics_location x,graphics_location y,color draw_color) {
    color old_color=POINT_COLOR;
    if (!lcd_display_state()) return;
    POINT_COLOR=draw_color;
    LCD_ShowChar(x,y,font,16,1);
    POINT_COLOR=old_color;
}

void graphics_draw_font_string(const char* string,graphics_location x,graphics_location y,color draw_color) {
    unsigned long draw_index=0;
    color old_color=POINT_COLOR;
    
    if (!lcd_display_state()) return;
    POINT_COLOR=draw_color;
    for (;draw_index<strlen(string);++draw_index)
        LCD_ShowChar(x+draw_index*FONT_WIDTH,y,*(string+draw_index),16,1);
    POINT_COLOR=old_color;
}
