static bool graphics_window_list_add(const unsigned int list_index,window_handle new_handle,window_name new_name,window_title new_title,window_background_color new_background_color,window_height new_height,window_width new_width,bool is_show_title,window_notice_recall new_recall_event) {
    widget_handle clear_widget_handle_index=0;
    if (list_index<MAX_WINDOW_AND_MESSAGE_COUNT && window_list.current_window_data[list_index].handle==INVALID_WINDOW_HANDLE) {
        window_list.current_window_data[list_index].background_color=new_background_color;
        window_list.current_window_data[list_index].x=0;
        window_list.current_window_data[list_index].y=0;
        window_list.current_window_data[list_index].handle=new_handle;
        window_list.current_window_data[list_index].height=new_height;
        window_list.current_window_data[list_index].width=new_width;
        window_list.current_window_data[list_index].show_stat=false;
        window_list.current_window_data[list_index].show_title=is_show_title;
        window_list.current_window_data[list_index].recall_event=new_recall_event;
        window_list.current_window_data[list_index].widget_count=0;
        memset(window_list.current_window_data[list_index].widget_list,0,MAX_WIDGET_COUNT*sizeof(window_widget));
        for (;clear_widget_handle_index<MAX_WIDGET_COUNT;++clear_widget_handle_index)
            window_list.current_window_data[list_index].widget_list[clear_widget_handle_index].widget_handle=INVALID_WIDGET_HANDLE;
        memcpy((void*)window_list.current_window_data[list_index].title,new_title,WINDOW_TITLE_LENGTH);
        memcpy((void*)window_list.current_window_data[list_index].name,new_name,WINDOW_NAME_LENGTH);
        return true;
    }
    return false;
}

static void graphics_window_list_clean(const unsigned int list_index) {
    widget_handle clear_widget_handle_index=0;
    if (list_index<MAX_WINDOW_AND_MESSAGE_COUNT)    {
        window_list.current_window_data[list_index].background_color=WINDOW_DEFUALT_BACKGROUND_COLOR;
        window_list.current_window_data[list_index].handle=INVALID_WINDOW_HANDLE;
        window_list.current_window_data[list_index].x=0;
        window_list.current_window_data[list_index].y=0;
        window_list.current_window_data[list_index].height=0;
        window_list.current_window_data[list_index].width=0;
        memset((void*)window_list.current_window_data[list_index].title,0,WINDOW_TITLE_LENGTH);
        memset((void*)window_list.current_window_data[list_index].name,0,WINDOW_NAME_LENGTH);
        window_list.current_window_data[list_index].recall_event=NULL;
        window_list.current_window_data[list_index].show_stat=false;
        window_list.current_window_data[list_index].show_title=false;
        window_list.current_window_data[list_index].widget_count=0;
        memset(window_list.current_window_data[list_index].widget_list,0,MAX_WIDGET_COUNT*sizeof(window_widget));
        for (;clear_widget_handle_index<MAX_WIDGET_COUNT;++clear_widget_handle_index)
            window_list.current_window_data[list_index].widget_list[clear_widget_handle_index].widget_handle=INVALID_WIDGET_HANDLE;
    }
}

void graphics_window_init(void) {
    unsigned int clean_window_index=0;
    
    window_list.current_window_count=0;
    for (;clean_window_index<MAX_WINDOW_AND_MESSAGE_COUNT;++clean_window_index)
        graphics_window_list_clean(clean_window_index);
}

