#define MAX_LENGTH_BOX_TITLE     16
#define MAX_LENGTH_BOX_CAPTION   32

#define DEFAULT_BOX_WIDTH       200
#define DEFAULT_BOX_HEIGHT      120
#define DEFAULT_BUTTON_WIDTH     60
#define DEFAULT_BUTTON_HEIGHT    20
#define DEFAULT_PICTURE_WIDTH    40
#define DEFAULT_PICTURE_HEIGHT   40

#define DISTANCE_BUTTON_TO_LABEL   5
#define DISTANCE_BUTTON_BETWEEN   20
#define DISTANCE_PICTURE_TO_TOP    8
#define DISTANCE_PICTURE_TO_LEFT  10
#define DISTANCE_PICTURE_TO_LABEL 16

messagebox_return click_code=no_click;

static void messagebox_ok_click_event(void) {
    click_code=click_ok;
}

static void messagebox_cancel_click_event(void) {
    click_code=click_cancel;
}

static void messagebox_notice_event(window_handle recall_window,notice_flag in_notice_flag,long parameter1,long parameter2) {
    switch (in_notice_flag) {
        case notice_click:
            break;
        case notice_close:
            click_code=click_exit;
            break;
    }
}

messagebox_return messagebox(window_height call_messagebox_window,const char* box_caption,const char* box_title,messagebox_style show_style) {
//  WARNING! 由于内存太小,所以label 用不了,改用直接将graphics_draw_font_string 把文字打印到窗口中 ..
    window_height messagebox_height=DEFAULT_BOX_HEIGHT;
    window_width  messagebox_width=DEFAULT_BOX_WIDTH;
    graphics_location messgaebox_x=0;
    graphics_location messgaebox_y=0;
    window_handle find_empty_window=0;
//    window_widget label;
//    widget_handle label_handle=INVALID_WIDGET_HANDLE;
    window_widget button_ok;
    widget_handle button_ok_handle=INVALID_WIDGET_HANDLE;
    window_widget button_cancel;
    widget_handle button_cancel_handle=INVALID_WIDGET_HANDLE;
    window_widget picture_warning;
    widget_handle picture_warning_handle=INVALID_WIDGET_HANDLE;
    char output_box_title[MAX_LENGTH_BOX_TITLE]={0};
    char output_box_caption[MAX_LENGTH_BOX_CAPTION]={0};
    char output_line[MAX_LENGTH_BOX_CAPTION/2+1]={0};

    click_code=no_click;
    
    if (NULL!=box_title) {
        if (strlen(box_title)<=MAX_LENGTH_BOX_TITLE)
            memcpy(output_box_title,box_title,strlen(box_title));
        else
            memcpy(output_box_title,box_title,MAX_LENGTH_BOX_TITLE);
    }
    if (NULL!=box_caption) {
        if (strlen(box_caption)<=MAX_LENGTH_BOX_CAPTION)
            memcpy(output_box_caption,box_caption,strlen(box_caption));
        else
            memcpy(output_box_caption,box_caption,MAX_LENGTH_BOX_CAPTION);
    }
    
    lcd_calcu_middle_location(0,0,SCREEN_HORIZONTAL_PIXEL,SCREEN_LONGITUDINAL_PIXEL,&messgaebox_x,&messgaebox_y);
    messgaebox_x-=DEFAULT_BOX_WIDTH/2;
    messgaebox_y-=DEFAULT_BOX_HEIGHT/2;
/*    
    label.widget_width=FONT_WIDTH*(MAX_LENGTH_BOX_CAPTION/2);
    label.widget_height=FONT_HEIGHT*2;
    label.x=messagebox_width/2-label.widget_width/2;
    label.y=messagebox_height/2-label.widget_height;
    label.widget_type=widget_label;
*/
    find_empty_window=create_window("messagebox",output_box_title,false,&messagebox_notice_event);
    window_list.current_window_data[find_empty_window].x=messgaebox_x;
    window_list.current_window_data[find_empty_window].y=messgaebox_y;
    window_list.current_window_data[find_empty_window].width=DEFAULT_BOX_WIDTH;
    window_list.current_window_data[find_empty_window].height=DEFAULT_BOX_HEIGHT;
    show_window(find_empty_window);
    
    if (show_style!=messagebox_warning) {
        if (strlen(output_box_caption)<(MAX_LENGTH_BOX_CAPTION/2)) {
            graphics_draw_font_string(output_box_caption,messgaebox_x+messagebox_width/2-(FONT_WIDTH*strlen(output_box_caption)/2),messgaebox_y+messagebox_height/2-FONT_HEIGHT,black);
        } else {
            memcpy(output_line,output_box_caption,MAX_LENGTH_BOX_CAPTION/2);
            graphics_draw_font_string(output_line,messgaebox_x+messagebox_width/2-(FONT_WIDTH*16)/2,messgaebox_y+messagebox_height/2-FONT_HEIGHT*2,black);
            memcpy(output_line,(const void*)((unsigned long)output_box_caption+MAX_LENGTH_BOX_CAPTION/2),MAX_LENGTH_BOX_CAPTION/2);
            graphics_draw_font_string(output_line,messgaebox_x+messagebox_width/2-(FONT_WIDTH*16)/2,messgaebox_y+messagebox_height/2-FONT_HEIGHT,black);
        }
    } else {
        if (strlen(output_box_caption)<(MAX_LENGTH_BOX_CAPTION/2)) {
            graphics_draw_font_string(output_box_caption,messgaebox_x+DISTANCE_PICTURE_TO_LEFT+DEFAULT_PICTURE_WIDTH+DISTANCE_PICTURE_TO_LABEL,messgaebox_y+messagebox_height/2-FONT_HEIGHT,black);
        } else {
            memcpy(output_line,output_box_caption,MAX_LENGTH_BOX_CAPTION/2);
            graphics_draw_font_string(output_line,messgaebox_x+DISTANCE_PICTURE_TO_LEFT+DEFAULT_PICTURE_WIDTH+DISTANCE_PICTURE_TO_LABEL,messgaebox_y+messagebox_height/2-FONT_HEIGHT*2,black);
            memcpy(output_line,(const void*)((unsigned long)output_box_caption+MAX_LENGTH_BOX_CAPTION/2),MAX_LENGTH_BOX_CAPTION/2);
            graphics_draw_font_string(output_line,messgaebox_x+DISTANCE_PICTURE_TO_LEFT+DEFAULT_PICTURE_WIDTH+DISTANCE_PICTURE_TO_LABEL,messgaebox_y+messagebox_height/2-FONT_HEIGHT,black);
        }
    }

    switch (show_style) {
        case messagebox_tips:
/*
            label_handle=create_widget(find_empty_window,&label);
            set_label_caption(find_empty_window,label_handle,output_box_caption);
            draw_widget(find_empty_window,label_handle);
*/
            button_ok.widget_width=DEFAULT_BUTTON_WIDTH;
            button_ok.widget_height=DEFAULT_BUTTON_HEIGHT;
            button_ok.x=messagebox_width/2-DEFAULT_BUTTON_WIDTH/2;
            button_ok.y=messagebox_height/2+DISTANCE_BUTTON_TO_LABEL;
            button_ok.widget_type=widget_button;
            button_ok_handle=create_widget(find_empty_window,&button_ok);
            set_button_click_recall(find_empty_window,button_ok_handle,&messagebox_ok_click_event);
            set_button_caption(find_empty_window,button_ok_handle,"OK");
            draw_widget(find_empty_window,button_ok_handle);
            break;
        case messagebox_question:
            button_ok.widget_width=DEFAULT_BUTTON_WIDTH;
            button_ok.widget_height=DEFAULT_BUTTON_HEIGHT;
            button_ok.x=messagebox_width/2-DISTANCE_BUTTON_BETWEEN/2-DEFAULT_BUTTON_WIDTH;
            button_ok.y=messagebox_height/2+DISTANCE_BUTTON_TO_LABEL;
            button_ok.widget_type=widget_button;
            button_ok_handle=create_widget(find_empty_window,&button_ok);
            set_button_click_recall(find_empty_window,button_ok_handle,&messagebox_ok_click_event);
            set_button_caption(find_empty_window,button_ok_handle,"OK");
            draw_widget(find_empty_window,button_ok_handle);
            button_cancel.widget_width=DEFAULT_BUTTON_WIDTH;
            button_cancel.widget_height=DEFAULT_BUTTON_HEIGHT;
            button_cancel.x=messagebox_width/2+DISTANCE_BUTTON_BETWEEN/2;
            button_cancel.y=messagebox_height/2+DISTANCE_BUTTON_TO_LABEL;
            button_cancel.widget_type=widget_button;
            button_cancel_handle=create_widget(find_empty_window,&button_cancel);
            set_button_click_recall(find_empty_window,button_cancel_handle,&messagebox_cancel_click_event);
            set_button_caption(find_empty_window,button_cancel_handle,"Cancel");
            draw_widget(find_empty_window,button_cancel_handle);
            break;
        case messagebox_warning:
            picture_warning.x=DISTANCE_PICTURE_TO_LEFT;
            picture_warning.y=DISTANCE_PICTURE_TO_TOP;
            picture_warning.widget_type=widget_picture;
            picture_warning_handle=create_widget(find_empty_window,&picture_warning);
            set_picture_bitmap(find_empty_window,picture_warning_handle,(bitmap_data)messagebox_warning_flag);
            draw_widget(find_empty_window,picture_warning_handle);
            button_ok.widget_width=DEFAULT_BUTTON_WIDTH;
            button_ok.widget_height=DEFAULT_BUTTON_HEIGHT;
            button_ok.x=messagebox_width/2-DISTANCE_BUTTON_BETWEEN/2-DEFAULT_BUTTON_WIDTH;
            button_ok.y=messagebox_height/2+DISTANCE_BUTTON_TO_LABEL;
            button_ok.widget_type=widget_button;
            button_ok_handle=create_widget(find_empty_window,&button_ok);
            set_button_click_recall(find_empty_window,button_ok_handle,&messagebox_ok_click_event);
            set_button_caption(find_empty_window,button_ok_handle,"OK");
            draw_widget(find_empty_window,button_ok_handle);
            button_cancel.widget_width=DEFAULT_BUTTON_WIDTH;
            button_cancel.widget_height=DEFAULT_BUTTON_HEIGHT;
            button_cancel.x=messagebox_width/2+DISTANCE_BUTTON_BETWEEN/2;
            button_cancel.y=messagebox_height/2+DISTANCE_BUTTON_TO_LABEL;
            button_cancel.widget_type=widget_button;
            button_cancel_handle=create_widget(find_empty_window,&button_cancel);
            set_button_click_recall(find_empty_window,button_cancel_handle,&messagebox_cancel_click_event);
            set_button_caption(find_empty_window,button_cancel_handle,"Cancel");
            draw_widget(find_empty_window,button_cancel_handle);
            break;
        default:
            break;
    }
    while (no_click==click_code) {
        if (lcd_is_touch()) {
            delay_ms(50);
            notice_window(find_empty_window,notice_click,lcd_touch_x(),lcd_touch_y());
        }
    }
    close_window(find_empty_window);
    refresh_window(call_messagebox_window);  //  TIPS! 到时候可以直接这样..window_front  
    return click_code;
}

